import React,{useState} from 'react'
import Dice from './Dice'
import './RollDice.css'

function RollDice({sides}) {

  const [state, setState] = useState({
    dice1: "one",
    dice2: "three",
    rolling: false,
    totalScore:4
  })

  const { dice1, dice2, rolling, totalScore } = useState;

  const roll = () => {
    const newDice1 = sides[Math.floor(Math.random() * sides.length)]
    const newDice2 = sides[Math.floor(Math.random() * sides.length)]
    console.log(newDice1);
    const score1 = Object.values(newDice1)
    const score2 = Object.values(newDice2)
    console.log(score1);
    setState({
      dice1: Object.keys(newDice1),
      dice2: Object.keys(newDice2),
      rolling: true,
      totalScore: score1[0] + score2[0]
      
      
    })

    setTimeout(() => {
      setState((prevState) => ({ ...prevState,rolling:true }))
    }, 1000);
  }

  return (
    <>
      <div className='roll-dice'>
        <div className='rolldice-container'>
          <Dice face="one" />
          <Dice face="three"/>
        </div>
        <button onClick={roll} disabled={rolling}>
          {rolling ? "Rolling...":"Roll Dice"}
        </button>
        <h2>Total Score: {totalScore}</h2>
      </div>
             
    </>
  )
}

RollDice.defaultProps = {
  sides: [
    { one: 1 },
    { two: 2 },
    { three: 3 },
    { four: 4 },
    { five: 5 },
    { six: 6 }
  ]
}

export default RollDice
